/*Table structure for table `p_ktinorm` */

DROP TABLE IF EXISTS `p_ktinorm`;

CREATE TABLE `p_ktinorm` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(64) NOT NULL COMMENT '指标名称',
  `chu` varchar(10) NOT NULL COMMENT '指标所属处室',
  `tuan` varchar(10) NOT NULL COMMENT '指标所属团队',
  `target` varchar(2048) DEFAULT NULL COMMENT '目标值',
  `score` double DEFAULT NULL COMMENT '分值',
  `rule` varchar(2048) DEFAULT NULL COMMENT '考核规则',
  PRIMARY KEY (`id`)
