/*Table structure for table `p_ktiscore` */

DROP TABLE IF EXISTS `p_ktiscore`;

CREATE TABLE `p_ktiscore` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ktiname` varchar(64) DEFAULT NULL COMMENT 'KTI指标名称',
  `target` varchar(1024) DEFAULT NULL COMMENT '目标值',
  `score` double DEFAULT NULL COMMENT '分值',
  `rule` varchar(512) DEFAULT NULL COMMENT '考核规则',
  `ktinumber` int(10) DEFAULT NULL COMMENT 'KTI指标编号',
  `name` varchar(32) DEFAULT NULL COMMENT '姓名',
  `newnumber` varchar(32) DEFAULT NULL COMMENT '员工新一代编号',
  `result1` double DEFAULT NULL COMMENT '评分人一打分',
  `rater1` varchar(32) DEFAULT NULL COMMENT '评分人一新一代编号',
  `remark1` varchar(256) DEFAULT NULL,
  `result2` double DEFAULT NULL COMMENT '评分人二打分',
  `rater2` varchar(32) DEFAULT NULL COMMENT '评分人二新一代编号',
  `remark2` varchar(256) DEFAULT NULL,
  `result3` double DEFAULT NULL COMMENT '评分人三打分',
  `rater3` varchar(32) DEFAULT NULL COMMENT '评分人三新一代编号',
  `remark3` varchar(256) DEFAULT NULL,
  `sum` double DEFAULT '0' COMMENT '最终得分',
  `year` int(11) DEFAULT NULL COMMENT '评分年度',
  `season` int(11) DEFAULT NULL COMMENT '评分季度',
  PRIMARY KEY (`id`)
